import React from 'react';
import { useParams } from 'react-router-dom';
import { UserCircleIcon, ArrowRightOnRectangleIcon, GiftIcon } from '@heroicons/react/24/solid';

const mockItem = {
  id: '1',
  title: 'Vintage Denim Jacket',
  description: 'A classic denim jacket in great condition. Perfect for layering in any season. Size M.',
  images: [
    'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=400&q=80',
    'https://images.unsplash.com/photo-1469398715555-76331a6c7fa0?auto=format&fit=crop&w=400&q=80',
    'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80',
  ],
  uploader: {
    name: 'Jane Doe',
    email: 'jane@example.com',
  },
  status: 'Available',
};

const ItemDetail: React.FC = () => {
  const { id } = useParams();
  // In a real app, fetch item by id
  const item = mockItem;
  return (
    <div className="p-8 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">{item.title}</h2>
      {/* Image Gallery */}
      <div className="carousel w-full mb-6 rounded-lg shadow">
        {item.images.map((img, idx) => (
          <div key={idx} id={`img${idx}`} className="carousel-item relative w-full">
            <img src={img} alt={item.title} className="w-full h-72 object-cover rounded-lg" />
            <div className="absolute flex justify-between transform -translate-y-1/2 left-2 right-2 top-1/2">
              <a href={`#img${(idx - 1 + item.images.length) % item.images.length}`} className="btn btn-circle btn-sm">❮</a>
              <a href={`#img${(idx + 1) % item.images.length}`} className="btn btn-circle btn-sm">❯</a>
            </div>
          </div>
        ))}
      </div>
      {/* Description & Uploader */}
      <div className="flex flex-col md:flex-row gap-8 mb-6">
        <div className="flex-1">
          <div className="mb-4 text-gray-700 text-lg">{item.description}</div>
          <div className="flex items-center gap-3 mb-2">
            <span className={`badge ${item.status === 'Available' ? 'badge-success' : 'badge-ghost'}`}>{item.status}</span>
          </div>
        </div>
        <div className="flex flex-col items-center bg-base-100 rounded-xl shadow p-4 min-w-[180px]">
          <UserCircleIcon className="w-10 h-10 text-primary mb-1" />
          <div className="font-semibold">{item.uploader.name}</div>
          <div className="text-xs text-gray-500">{item.uploader.email}</div>
        </div>
      </div>
      {/* Actions */}
      <div className="flex gap-4">
        <button className="btn btn-primary flex items-center gap-2"><ArrowRightOnRectangleIcon className="w-5 h-5" />Swap Request</button>
        <button className="btn btn-accent flex items-center gap-2"><GiftIcon className="w-5 h-5" />Redeem via Points</button>
      </div>
    </div>
  );
};

export default ItemDetail;
