import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useUser } from './UserContext';
import { UserCircleIcon } from '@heroicons/react/24/solid';

const Navbar: React.FC = () => {
  const location = useLocation();
  const { role, loginAsUser, loginAsAdmin, logout } = useUser();
  const navLinks = [
    { to: '/', label: 'Home' },
    { to: '/dashboard', label: 'Dashboard' },
    { to: '/add', label: 'Add Item' },
  ];
  if (role === 'admin') navLinks.push({ to: '/admin', label: 'Admin Panel' });

  return (
    <nav className="navbar bg-base-100 shadow-md px-6 sticky top-0 z-50">
      <div className="flex-1">
        <Link to="/" className="text-2xl font-bold text-primary">ReWear</Link>
      </div>
      <div className="flex-none flex items-center gap-4">
        <ul className="menu menu-horizontal px-1 gap-2">
          {navLinks.map(link => (
            <li key={link.to}>
              <Link
                to={link.to}
                className={
                  location.pathname === link.to
                    ? 'active font-semibold text-accent'
                    : ''
                }
              >
                {link.label}
              </Link>
            </li>
          ))}
        </ul>
        {/* User/Profile Menu */}
        <div className="dropdown dropdown-end">
          <label tabIndex={0} className="btn btn-ghost btn-circle avatar">
            <UserCircleIcon className="w-8 h-8 text-primary" />
          </label>
          <ul tabIndex={0} className="mt-3 p-2 shadow menu menu-sm dropdown-content bg-base-100 rounded-box w-52">
            {role === null && (
              <>
                <li><button onClick={loginAsUser}>Log in as User</button></li>
                <li><button onClick={loginAsAdmin}>Log in as Admin</button></li>
              </>
            )}
            {role && (
              <>
                <li><span className="font-semibold">Role: {role}</span></li>
                <li><button onClick={logout}>Logout</button></li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
