import React from 'react';
import { UserCircleIcon, StarIcon, ArrowPathIcon } from '@heroicons/react/24/solid';

const mockProfile = {
  name: 'Jane Doe',
  email: 'jane@example.com',
  points: 120,
};

const mockItems = [
  {
    id: '1',
    title: 'Vintage Denim Jacket',
    image: 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=400&q=80',
    status: 'Available',
  },
  {
    id: '2',
    title: 'Floral Summer Dress',
    image: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80',
    status: 'Swapped',
  },
];

const mockSwaps = [
  {
    id: 's1',
    item: 'Cozy Knit Sweater',
    status: 'Completed',
  },
  {
    id: 's2',
    item: 'Classic White Tee',
    status: 'Ongoing',
  },
];

const Dashboard: React.FC = () => (
  <div className="p-8 max-w-4xl mx-auto">
    <h2 className="text-2xl font-bold mb-6">User Dashboard</h2>
    {/* Profile Section */}
    <div className="flex items-center gap-6 mb-8 bg-base-100 rounded-xl shadow p-6">
      <UserCircleIcon className="w-16 h-16 text-primary" />
      <div>
        <div className="text-xl font-semibold">{mockProfile.name}</div>
        <div className="text-gray-500">{mockProfile.email}</div>
      </div>
      <div className="ml-auto flex items-center gap-2 bg-yellow-100 text-yellow-800 px-4 py-2 rounded-full font-semibold">
        <StarIcon className="w-5 h-5" />
        {mockProfile.points} Points
      </div>
    </div>
    {/* Uploaded Items Section */}
    <div className="mb-8">
      <h3 className="text-lg font-semibold mb-2">Uploaded Items</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        {mockItems.map(item => (
          <div key={item.id} className="card bg-base-100 shadow-md rounded-xl overflow-hidden">
            <figure><img src={item.image} alt={item.title} className="h-40 w-full object-cover" /></figure>
            <div className="card-body p-4">
              <h4 className="card-title text-lg">{item.title}</h4>
              <span className={`badge ${item.status === 'Available' ? 'badge-success' : 'badge-ghost'}`}>{item.status}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
    {/* Swaps Section */}
    <div>
      <h3 className="text-lg font-semibold mb-2">Ongoing & Completed Swaps</h3>
      <ul className="timeline timeline-vertical">
        {mockSwaps.map(swap => (
          <li key={swap.id} className="mb-4">
            <div className="timeline-start font-semibold">{swap.item}</div>
            <div className={`timeline-middle ${swap.status === 'Completed' ? 'bg-success' : 'bg-info'} w-6 h-6 rounded-full flex items-center justify-center`}>
              <ArrowPathIcon className="w-4 h-4 text-white" />
            </div>
            <div className="timeline-end">
              <span className={`badge ${swap.status === 'Completed' ? 'badge-success' : 'badge-info'}`}>{swap.status}</span>
            </div>
          </li>
        ))}
      </ul>
    </div>
  </div>
);

export default Dashboard;
