import React from 'react';
import { useNavigate } from 'react-router-dom';

const featuredItems = [
  {
    id: '1',
    title: 'Vintage Denim Jacket',
    image: 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=400&q=80',
  },
  {
    id: '2',
    title: 'Floral Summer Dress',
    image: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80',
  },
  {
    id: '3',
    title: 'Cozy Knit Sweater',
    image: 'https://images.unsplash.com/photo-1469398715555-76331a6c7fa0?auto=format&fit=crop&w=400&q=80',
  },
];

const LandingPage: React.FC = () => {
  const navigate = useNavigate();
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-green-100 to-blue-100 py-12">
      <h1 className="text-4xl font-bold mb-4">ReWear – Community Clothing Exchange</h1>
      <p className="mb-8 text-lg max-w-xl text-center">
        Exchange unused clothing through direct swaps or a point-based system. Promote sustainable fashion and reduce textile waste!
      </p>
      <div className="flex gap-4 mb-8">
        <button onClick={() => navigate('/dashboard')} className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition">Start Swapping</button>
        <button onClick={() => navigate('/dashboard')} className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition">Browse Items</button>
        <button onClick={() => navigate('/add')} className="px-6 py-2 bg-yellow-500 text-white rounded hover:bg-yellow-600 transition">List an Item</button>
      </div>
      <div className="w-full max-w-2xl">
        <h2 className="text-2xl font-semibold mb-4 text-center">Featured Items</h2>
        <div className="carousel w-full rounded-box shadow-lg">
          {featuredItems.map((item, idx) => (
            <div key={item.id} id={`slide${idx}`} className="carousel-item relative w-full flex flex-col items-center">
              <img src={item.image} alt={item.title} className="w-full h-64 object-cover rounded-lg" />
              <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 bg-white bg-opacity-80 px-4 py-2 rounded shadow text-lg font-semibold">
                {item.title}
              </div>
              <div className="absolute flex justify-between transform -translate-y-1/2 left-2 right-2 top-1/2">
                <a href={`#slide${(idx - 1 + featuredItems.length) % featuredItems.length}`} className="btn btn-circle btn-sm">❮</a>
                <a href={`#slide${(idx + 1) % featuredItems.length}`} className="btn btn-circle btn-sm">❯</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
