import React, { useState } from 'react';
import { CheckCircleIcon, XCircleIcon, TrashIcon } from '@heroicons/react/24/solid';

const mockItems = [
  {
    id: '1',
    title: 'Vintage Denim Jacket',
    uploader: 'Jane Doe',
    status: 'Pending',
    image: 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=400&q=80',
  },
  {
    id: '2',
    title: 'Floral Summer Dress',
    uploader: 'John Smith',
    status: 'Approved',
    image: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80',
  },
  {
    id: '3',
    title: 'Cozy Knit Sweater',
    uploader: 'Alice Lee',
    status: 'Rejected',
    image: 'https://images.unsplash.com/photo-1469398715555-76331a6c7fa0?auto=format&fit=crop&w=400&q=80',
  },
];

const AdminPanel: React.FC = () => {
  const [items, setItems] = useState(mockItems);

  const handleAction = (id: string, action: 'approve' | 'reject' | 'remove') => {
    setItems(prev =>
      action === 'remove'
        ? prev.filter(item => item.id !== id)
        : prev.map(item =>
            item.id === id
              ? { ...item, status: action === 'approve' ? 'Approved' : 'Rejected' }
              : item
          )
    );
  };

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
      <div className="overflow-x-auto bg-base-100 rounded-xl shadow">
        <table className="table w-full">
          <thead>
            <tr>
              <th>Image</th>
              <th>Title</th>
              <th>Uploader</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
              <tr key={item.id}>
                <td><img src={item.image} alt={item.title} className="w-16 h-16 object-cover rounded" /></td>
                <td>{item.title}</td>
                <td>{item.uploader}</td>
                <td>
                  <span className={`badge ${item.status === 'Approved' ? 'badge-success' : item.status === 'Rejected' ? 'badge-error' : 'badge-warning'}`}>{item.status}</span>
                </td>
                <td className="flex gap-2">
                  <button
                    className="btn btn-xs btn-success flex items-center gap-1"
                    onClick={() => handleAction(item.id, 'approve')}
                    disabled={item.status === 'Approved'}
                  >
                    <CheckCircleIcon className="w-4 h-4" /> Approve
                  </button>
                  <button
                    className="btn btn-xs btn-error flex items-center gap-1"
                    onClick={() => handleAction(item.id, 'reject')}
                    disabled={item.status === 'Rejected'}
                  >
                    <XCircleIcon className="w-4 h-4" /> Reject
                  </button>
                  <button
                    className="btn btn-xs btn-outline btn-warning flex items-center gap-1"
                    onClick={() => handleAction(item.id, 'remove')}
                  >
                    <TrashIcon className="w-4 h-4" /> Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPanel;
